// @flow
import React, { useState, useEffect } from "react"

//Bootstrap and jQuery libraries
import 'bootstrap/dist/css/bootstrap.min.css';
import 'jquery/dist/jquery.min.js';
//Datatable Modules
import "datatables.net-dt/js/dataTables.dataTables"
import "datatables.net-dt/css/jquery.dataTables.min.css"
import $ from 'jquery'; 

import UserApiService from "../api/UserApi";
import axios from 'axios';

import {
  Container,
  Grid,
  Card,
  Button,
  Form,
  Avatar,
  Profile,
  List,
  Media,
  Text,
  Comment,
  Page
} from "tabler-react";

import SiteWrapper from "../SiteWrapper.react";

function UserListPage() {
  let [userlist,userlist변경] = useState([]);

  let user = {
    'userid' : "",
    'email' : "",
    'kakaoname' : "",
    'nickname' : "",
    'phone' : "",
    'userdate' : "",
    'subscripstat' : "",
    'managestat' : ""
  }
  let [userdata,userdata변경] = useState(user);



    useEffect(() => {
      console.log("유즈이펙트 실행")

      userlistget();
    },[]);


    function userdelete(userdata){
      UserApiService.deleteUser(userdata)
      .then(res => {             
          console.log('***** Admin deleteUser success:', res.data);
          $('#example').DataTable().destroy();
          userlistget();
          // window.location.reload(true);
        })
      .catch(err => {
        console.log('***** Admin deleteUser error:', err);
      }); 
    }

  function userlistget(){
    UserApiService.fetchUsers()
    .then(res => {
      console.log(res.data)
      console.log("fetchUsers 성공")                
      userlist변경(res.data);

      setTimeout(()=>{                        
        $('#example').DataTable(
            {
              "lengthMenu": [[5, 10, 15, -1], [5, 10, 15, "All"]]
              // destroy: true
            }
          );
        },100)
      })
    .catch(err => {
      console.log('***** Admin fetchUsers error:', err);
    }); 
  }



  return (
<SiteWrapper>
<Page.Content title="User LIST">
        <Grid.Row>
          <Grid.Col md={12} xl={12}>
            <Card

              title="This is a standard card"
              isCollapsible
              // isClosable
              body="Lorem ipsum dolor sit amet, consectetur adipisicing elit.
            Aperiam deleniti fugit incidunt, iste, itaque minima neque
            pariatur perferendis sed suscipit velit vitae voluptatem. A
            consequuntur, deserunt eaque error nulla temporibus!"
              footer="This is standard card footer"
            />
            {/* <div className="container"> */}

          <table id="example" className="display">
            <thead>
                <tr>
                    <th>유저ID</th>
                    <th>이메일</th>
                    <th>카카오닉네임</th>
                    <th>홈닉네임</th>
                    <th>핸드폰</th>
                    <th>가입일</th>
                    <th>구독</th>
                    <th>차단</th>
                    <th>수정</th>
                    <th>삭제</th>
                </tr>
            </thead>
            <tbody>

                <tr>
                    <td>Tiger Nixon</td>
                    <td>System Architect</td>
                    <td>Edinburgh</td>
                    <td>61</td>
                    <td>2011/04/25</td>
                    <td>$320,800</td>
                    <td>2011/04/25</td>
                    <td>$320,800</td>
                    <td>2011/04/25</td>
                    <td>$320,800</td>
                </tr>
              {
                userlist.map(function(data){
                return(
                  <tr>
                      <td>{data['userid']}</td>
                      <td>{data['email']}</td>
                      <td>{data['kakaoname']}</td>
                      <td>{data['nickname']}</td>
                      <td>{data['phone']}</td>
                      <td>{data['userdate'].substring(0,19)}</td>
                      <td>{data['subscripstat'] === 1 ? "구독" : "-" }</td>
                      <td>{data['managestat']  === 1 ? "차단" : "-" }</td>
                      <td> <Button color="yellow" onClick = {() => {console.log("asd")}}>x</Button> </td>
                      <td>  <Button color="red" onClick = {() => {userdelete(data['userid'])}}>x</Button> </td>
                  </tr>
                        )
                      })
              }

            </tbody>
            <tfoot>
                <tr>
                  <th>유저ID</th>
                    <th>이메일</th>
                    <th>카카오닉네임</th>
                    <th>홈닉네임</th>
                    <th>핸드폰</th>
                    <th>가입일</th>
                    <th>구독</th>
                    <th>차단</th>
                    <th>수정</th>
                    <th>삭제</th>
                </tr>
            </tfoot>
        </table>

        {/* </div> */}

          </Grid.Col>
          {/* <Grid.Col md={6} xl={4}>
            <Card
              title="Built card"
              isCollapsible
              isClosable
              body="Lorem ipsum dolor sit amet, consectetur adipisicing elit.
            Aperiam deleniti fugit incidunt, iste, itaque minima neque
            pariatur perferendis sed suscipit velit vitae voluptatem. A
            consequuntur, deserunt eaque error nulla temporibus!"
            />
          </Grid.Col>
          <Grid.Col md={6} xl={4}>
            <Card
              title="Card blue"
              isCollapsible
              isClosable
              statusColor="blue"
              body="Lorem ipsum dolor sit amet, consectetur adipisicing elit.
            Aperiam deleniti fugit incidunt, iste, itaque minima neque
            pariatur perferendis sed suscipit velit vitae voluptatem. A
            consequuntur, deserunt eaque error nulla temporibus!"
            />
        	</Grid.Col> */}
        </Grid.Row>
      </Page.Content>
    </SiteWrapper>
  );
}

export default UserListPage;
